import { Component } from "react";
import './index.css'

const style = {
  marginTop: '5px',
  padding: '5px 0',
  boxShadow: '0 0 3px #eee'
}

export default class ToDoItem extends Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }

  render() {
    return (
      <div className="item" style={style} >
        <input type="checkbox" />
        {this.props.item}
      </ div >
    )
  }


}
