import { Component } from "react";
import './index.css'

const style = {
  marginTop: '5px',
  padding: '5px 0',
  boxShadow: '0 0 3px #eee'
}

export default class ToDoItem extends Component {


  render() {
    return (
      <div className="item" style={style} >
        <input type="checkbox" onChange={this.handlefinish.bind(this, this.props.checked)} />
        {this.props.item}
      </ div >
    )
  }

  handlefinish(check) {
    // console.log(index);
    let checked = !check;
    if (checked) {
      this.props.handlefinish();
    } else {
      this.props.handleUnfinish();
    }


  }


}
