import { Component, Fragment } from "react";
import ToDoItem from "../ToDoItem"
import './index.css'

const style = {
  marginTop: '20px'
}

export default class ToDoList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      inputValue: '',
      list: []
    }
    this.handleChange = this.handleChange.bind(this)
  }


  render() {
    return (
      <Fragment>
        <div className="list" style={style}>
          <input type="text" placeholder="请输入代办事项"
            value={this.state.inputValue}
            onChange={this.handleChange} //this指向组件
          />
          <button onClick={this.handleClick.bind(this)}>添加</button>
        </div >
        <ul>
          {
            this.state.list.map((item, index) => {
              return (
                <div key={index} className="item">

                  <ToDoItem item={item} index={index} deleteItem={this.handleDelete.bind(this)} />
                  <button onClick={this.handleDelete.bind(this)} className="delete-btn">删除</button>
                </div>
              )
            })
          }
        </ul>
      </Fragment>
    )
  }
  handleChange(e) {
    // console.log(this); undefined this指向有问题
    // console.log(e.target.value);
    this.setState({
      inputValue: e.target.value
    })
  }

  handleClick() {
    this.setState({
      list: [...this.state.list, this.state.inputValue],
      inputValue: '' //在点击添加按钮后，把input框清空
    })
  }

  handleDelete(index) {
    let list = [...this.state.list]; //list做一个拷贝
    list.splice(index, 1);
    this.setState({
      list
    })
  }
}
